    /*
     * Listing of selisih_stok
     */
    function index()
    {
        $data['selisih_stok'] = $this->Selisih_stok_model->get_all_selisih_stok();

        $data['_view'] = 'selisih_stok/index';
        $this->load->view('layouts/main',$data);
